const Steps = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const containerRef = useRef(null);

  const handleScroll = () => {
    const stepsArray = Array.from(containerRef.current?.children || []);
    const scrollPosition = window.scrollY + window.innerHeight / 2; // Center position of the viewport

    let stepIndex = 0;

    // Find the step closest to the center of the viewport
    stepsArray.forEach((step, index) => {
      const rect = step.getBoundingClientRect();
      const stepTop = rect.top + window.scrollY;

      // If scroll position is greater than the step's top position, update stepIndex
      if (scrollPosition >= stepTop) {
        stepIndex = index;
      }
    });

    setCurrentStep(stepIndex - 1); // Adjust index if necessary
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div
      className="scroll-container flex flex-col items-center justify-center min-h-screen"
      ref={containerRef}
    >
      <header className="text-9xl text-center text-blue-900 tracking-[19.2px] max-md:max-w-full max-md:text-4xl mt-10 font-bold">
        <h1>PAPER HEART STEPS</h1>
      </header>
      {steps.map((step, index) => (
        <div
          className={`step flex flex-col items-center justify-center min-h-[75vh] mb-4 p-4 text-center transition-transform duration-500 ${
            index === currentStep ? "scale-105 opacity-100" : "opacity-50"
          }`}
          key={index}
        >
          <h1 className="text-3xl font-bold mb-4">{step.text}</h1>
          <div className="svg-container">{step.svg}</div>
        </div>
      ))}
    </div>
  );
};

export default Steps;
